using System;
using System.Collections;
using System.Collections.Generic;
using System.Diagnostics;
using System.Linq;
using System.Text;

namespace ProjectEuler
{
    // class for testing other problems
    class ProblemTest
    {
        public void Method()
        {
            var input = "";
            var expectedAnswer = 0;
            int iters = 25000;

            // Func<input type, output type>
            Func<string, IEnumerable<string>> function = repeatedDNASequences2;

            Stopwatch stop = Stopwatch.StartNew();
            var ans = function(input);
            for (int i = 0; i < iters - 1; i++)
                ans = function(input);
            stop.Stop();

            Console.WriteLine($"Answer: {ans}\n\n" +
                              //$"Correct: {Equals(ans, expectedAnswer)}\n" +
                              $"Iterations: {iters}\n" +
                              $"Time per iteration: {stop.ElapsedMilliseconds * 1000 / iters} us\n" +
                              $"Total time: {stop.ElapsedMilliseconds} ms\n");
        }

        // 480-500
        string[] repeatedDNASequences(string s)
        {
            var d = new HashSet<string>();
            var r = new HashSet<string>();

            for (int i = 0; i < s.Length - 9; i++)
            {
                var u = s.Substring(i, 10);
                if (!d.Add(u))
                    r.Add(u);
            }

            return r.OrderBy(x => x).ToArray();
        }

        public string[] repeatedDNASequences2(String s)
        {
            var result = new List<string>();
            var word = new HashSet<int>();
            var secondWord = new HashSet<int>();
            int[] map = new int[26];
            map['C' - 'A'] = 1;
            map['G' - 'A'] = 2;
            map['T' - 'A'] = 3;
            int value = 0;
            for (int i = 0; i < s.Length; i++)
            {
                value <<= 2;
                value |= map[s[i] - 'A'];
                value &= 0xfffff;
                if (i < 9)
                    continue;
                if (!word.Add(value) && secondWord.Add(value))
                    result.Add(s.Substring(i - 9, 10));
            }
            return result.OrderBy(x => x).ToArray();
        }

    }
}